const rotateAnimation = keyframes`
  from { transform: rotate(0deg) }
  to { transform: rotate(360deg) }
`

export const TableChunk = styled.div`
  margin-top: 24px; 
  color: #fff;
  background: #1D1D1F;
  box-shadow: inset 0px -1px 0px 0px #414142;
  overflow-x: auto;

  table {
    width: 100%;
    border-collapse: collapse;
  }

  &.loading {
    position: relative;

    &::before {
      content: '';
      position: absolute;
      left: 0;
      right: 0;
      top: 0;
      height: 100%;
      background: rgba(0, 0, 0, .7);
    }

    &::after {
      content: '';
      animation: ${rotateAnimation} 1.6s linear infinite;
      position: absolute;
      width: 64px;
      height: 64px;
      left: 50%;
      top: 50%;
      margin: -32px 0 0 -32px;
      background: url(data:image/png;base64,);
      background-size: 100%;
    }
  }

  thead {
    height: 48px;
    background: #2A2A2C;
  }

  th, td {
    ${font14};
    border: none;
  }

  td {
    padding: 12px 16px;
    box-shadow: inset 0px -1px 0px 0px #414142;
    height: 68px;
    line-height: 22px;
  }

  th {
    padding: 0 16px;
    text-align: left;
    color: #8E8E8F;
  }

  .status {
    ${flexAlign};

    i {
      margin-right: 8px;
    }
  }

  .break-word {
    word-break: break-word;
    max-width: 260px;
    display: inline-block;
  }
`
